    /**
     * Creates an instance of JFighterProject with 2 Fighters with stats that
     * the player entered on this Form.
     */
    private void startGame() {
        boolean isInputOk = true;
        int health1 = 0;
        int armor1 = 0;
        int dice1 = 0;
        
        int health2 = 0;
        int armor2 = 0;
        int dice2 = 0;
        
        /**
         * Create two Fighters from the information from the player.
         * If any numeric field is not a number, do not start the game.
         * Instead, display an error message and have the player enter values again.
         */
        try {
            health1 = Integer.parseInt(this.jTextFieldHealth1.getText());
            armor1 = Integer.parseInt(this.jTextFieldArmor1.getText());
            dice1 = Integer.parseInt(this.jTextFieldDice1.getText());

            health2 = Integer.parseInt(this.jTextFieldHealth2.getText());
            armor2 = Integer.parseInt(this.jTextFieldArmor2.getText());
            dice2 = Integer.parseInt(this.jTextFieldDice2.getText());
        }
        catch (NumberFormatException ex) {
            JOptionPane.showMessageDialog(rootPane, "Health, armor, and/or dice must be numbers.");
            isInputOk = false;
        }
        
        // start the game once the input is valid
        if (isInputOk) {
            Fighter fighter1 = new Fighter(this.jTextFieldName1.getText(), health1, armor1, dice1);
            Fighter fighter2 = new Fighter(this.jTextFieldName2.getText(), health2, armor2, dice2);
            
            // instantiate the actual game Form.
            JFighterProject jfp = new JFighterProject(fighter1, fighter2);
            jfp.setVisible(true);
            this.setVisible(false);
        }
    }
    
    /**
     * This starts the game once the user clicks on the "Start Game" button
     * @param evt
     */
    private void jButtonStartGameActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButtonStartGameActionPerformed
        // TODO add your handling code here:
        this.startGame();
    }//GEN-LAST:event_jButtonStartGameActionPerformed

