    /**
     * This allows the player to press the enter key to activate the "Start Game"
     * button
     * @param evt the KeyEvent containing the KeyCode of the key pressed.
     */
    private void jButtonStartGameKeyPressed(java.awt.event.KeyEvent evt) {//GEN-FIRST:event_jButtonStartGameKeyPressed
       // KeyCode of 10 is the Enter key
       if (evt.getKeyCode() == 10) {
           this.startGame();
       }
    }//GEN-LAST:event_jButtonStartGameKeyPressed
 
